    private void buttonConnectionNeo4jActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonConnectionNeo4jActionPerformed
        try {
            Neo4jAdministration neo4j = new  Neo4jAdministration();
            boolean connection = neo4j.connectionNeo4j();
            
            if(connection){
                textOkNeo4j.setText("Connessione riuscita con Neo4j!");
                textOkNeo4j.setFont(new java.awt.Font("Arial", 1, 12));
                textOkNeo4j.setForeground(new java.awt.Color(10, 1, 1));
            }else{
                textOkNeo4j.setText("Errore in fase di connessione con Neo4j");
            }
        } catch (Exception ex) {
            Logger.getLogger(GUIbenchmark.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_buttonConnectionNeo4jActionPerformed

    private void buttonConnectionHBaseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonConnectionHBaseActionPerformed
        // TODO add your handling code here:
        try {
            HbaseAdministration hbase = new HbaseAdministration();
            boolean connection = hbase.connectionHBase();
            
            if(connection){
               textConnHbase.setText("Connessione riuscita con HBase!");
               textConnHbase.setFont(new java.awt.Font("Arial", 1, 12));
               textConnHbase.setForeground(new java.awt.Color(10, 1, 1));
               buttonPutValue.setEnabled(true);//Attivo bottone per aggiungere righe alle column family
            }else{
                textConnHbase.setText("Errore in fase di connessione con Hbase");
            }
        } catch (IOException ex) {
            Logger.getLogger(GUIbenchmark.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_buttonConnectionHBaseActionPerformed

    private void buttonPutValueActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonPutValueActionPerformed
        // TODO add your handling code here:
        HbaseAdministration hbase;
        try {
            hbase = new HbaseAdministration();
            hbase.putValuePerson();//inserimento righe columnFamily Person
            hbase.putValueSociety();//inserimento righe columnFamily Society
            hbase.putValueBankAccount();//inserimento righe columnFamily BankAccount
        } catch (IOException ex) {
            Logger.getLogger(GUIbenchmark.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_buttonPutValueActionPerformed

